import React from 'react';

function TextBoxEditor({ textBox, onUpdate, onRemove }) {
  if (!textBox) return null;

  const handleChange = (e) => {
    const { name, value } = e.target;
    onUpdate(textBox.id, { [name]: name === 'fontSize' ? parseInt(value, 10) : value });
  };

  return (
    <div className="text-box-editor">
      <h3>Edit Text</h3>

      <div className="form-group">
        <label htmlFor="text">Text Content:</label>
        <textarea
          id="text"
          name="text"
          value={textBox.text}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label htmlFor="fontSize">Font Size:</label>
        <input
          type="range"
          id="fontSize"
          name="fontSize"
          min="12"
          max="72"
          value={textBox.fontSize}
          onChange={handleChange}
        />
        <span>{textBox.fontSize}px</span>
      </div>

      <div className="form-group">
        <label htmlFor="fontFamily">Font:</label>
        <select
          id="fontFamily"
          name="fontFamily"
          value={textBox.fontFamily}
          onChange={handleChange}
        >
          <option value="Impact">Impact</option>
          <option value="Arial">Arial</option>
          <option value="Comic Sans MS">Comic Sans MS</option>
          <option value="Times New Roman">Times New Roman</option>
        </select>
      </div>

      <div className="form-group">
        <label htmlFor="color">Text Color:</label>
        <input
          type="color"
          id="color"
          name="color"
          value={textBox.color}
          onChange={handleChange}
        />
      </div>

      <div className="form-group">
        <label htmlFor="stroke">Stroke Color:</label>
        <input
          type="color"
          id="stroke"
          name="stroke"
          value={textBox.stroke}
          onChange={handleChange}
        />
      </div>

      <button 
        className="btn btn-danger" 
        onClick={() => onRemove(textBox.id)}
      >
        Remove Text Box
      </button>
    </div>
  );
}

export default TextBoxEditor;
